// Tell the TS compiler that it's ok to load these
// types of extensions.  FuseBox will do the right thing.
declare module '*.jpeg' {
  export default ''
}

declare module '*.jpg' {
  export default ''
}

declare module '*.gif' {
  export default ''
}

declare module '*.png' {
  export default ''
}

declare module '*.svg' {
  export default ''
}
